_.namespace("js.homepage2013", function (c) {
    c.bootstrap = function () {
        var e = $(window),
            c = $(".parallax"),
            d = $(".hero");
        $(".testimonials");
        var f = ["segmentation", "funnels", "retention", "people", "notifications"],
            g = f.length;
        $(function () {
            $(".home").addClass("ready")
        });
        var h = window.location.hash || "#segmentation";
        _.each(f, function (a) {
            h === "#" + a && (d.removeClass("current"), $("." + a).addClass("current"))
        });
        e.on("scroll", function () {
            var a = e.scrollTop();
            800 < a || c.css({
                "margin-top": -~~(0.5 * a) + "px"
            })
        });
        var b = 0,
            b = $(".hero").index($(".hero.current"));
        $(".arrow").click(function () {
            var a = $(this).hasClass("right") ? 1 : -1;
            $(d[b]).removeClass("current");
            b = (b + a + g) % g;
            $(d[b]).addClass("current")
        });
        $(".lessons-learned .block").hover(function () {
            var a = $(this),
                b = a.find("p").height();
            15 < a.find("h3").height() && (b += 15);
            a.stop().animate({
                "margin-top": -b + "px",
                "padding-bottom": b + "px"
            }, 100);
            a.find("p").stop().animate({
                opacity: 1
            }, 100)
        }, function () {
            var a = $(this);
            a.stop().animate({
                "margin-top": 0,
                "padding-bottom": 0
            }, 100);
            a.find("p").stop().animate({
                opacity: 0
            }, 100)
        })
    }
});